import React from "react";
import {
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
  MDBIcon,
  MDBTextArea,
} from "mdb-react-ui-kit";

export default function ChatScreen() {
  return (
    React.createElement(MDBContainer, { className: "py-5" },
      React.createElement(MDBRow, { className: "d-flex justify-content-center" },
        React.createElement(MDBCol, { md: "8", lg: "6", xl: "4" },
          React.createElement(MDBCard, { id: "chat1", style: { borderRadius: "15px" } },
            React.createElement(MDBCardHeader, {
              className: "d-flex justify-content-between align-items-center p-3 bg-info text-white border-bottom-0",
              style: { borderTopLeftRadius: "15px", borderTopRightRadius: "15px" }
            },
              React.createElement(MDBIcon, { fas: true, icon: "angle-left" }),
              React.createElement("p", { className: "mb-0 fw-bold" }, "Live chat"),
              React.createElement(MDBIcon, { fas: true, icon: "times" })
            ),
            React.createElement(MDBCardBody, null,
              React.createElement("div", { className: "d-flex flex-row justify-content-start mb-4" },
                React.createElement("img", {
                  src: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp",
                  alt: "avatar 1",
                  style: { width: "45px", height: "100%" }
                }),
                React.createElement("div", {
                  className: "p-3 ms-3",
                  style: {
                    borderRadius: "15px",
                    backgroundColor: "rgba(57, 192, 237,.2)"
                  }
                },
                  React.createElement("p", { className: "small mb-0" },
                    "Hello and thank you for visiting MDBootstrap. Please click the video below."
                  )
                )
              ),
              React.createElement("div", { className: "d-flex flex-row justify-content-end mb-4" },
                React.createElement("div", {
                  className: "p-3 me-3 border",
                  style: { borderRadius: "15px", backgroundColor: "#fbfbfb" }
                },
                  React.createElement("p", { className: "small mb-0" },
                    "Thank you, I really like your product."
                  )
                ),
                React.createElement("img", {
                  src: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava2-bg.webp",
                  alt: "avatar 1",
                  style: { width: "45px", height: "100%" }
                })
              ),
              React.createElement("div", { className: "d-flex flex-row justify-content-start mb-4" },
                React.createElement("img", {
                  src: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp",
                  alt: "avatar 1",
                  style: { width: "45px", height: "100%" }
                }),
                React.createElement("div", { className: "ms-3", style: { borderRadius: "15px" } },
                  React.createElement("div", { className: "bg-image" },
                    React.createElement("img", {
                      src: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/screenshot1.webp",
                      style: { borderRadius: "15px" },
                      alt: "video"
                    }),
                    React.createElement("a", { href: "#!" },
                      React.createElement("div", { className: "mask" })
                    )
                  )
                )
              ),
              React.createElement("div", { className: "d-flex flex-row justify-content-start mb-4" },
                React.createElement("img", {
                  src: "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava1-bg.webp",
                  alt: "avatar 1",
                  style: { width: "45px", height: "100%" }
                }),
                React.createElement("div", {
                  className: "p-3 ms-3",
                  style: {
                    borderRadius: "15px",
                    backgroundColor: "rgba(57, 192, 237,.2)"
                  }
                },
                  React.createElement("p", { className: "small mb-0" }, "...")
                )
              ),
              React.createElement(MDBTextArea, {
                className: "form-outline",
                label: "Type your message",
                id: "textAreaExample",
                rows: 4
              })
            )
          )
        )
      )
    )
  );
}
